PREFIX dbo: <http://dbpedia.org/ontology/>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

SELECT ?country ?name ?value
WHERE {
    ?country a dbo:Country ;
             rdfs:label ?name ;
             <http://dbpedia.org/ontology/populationTotal> ?value .
    FILTER(lang(?name) = 'en')
}
ORDER BY DESC(?value)
LIMIT 20

